const audioContext = new (window.AudioContext || window.webkitAudioContext)();

function playCarPassSound() {
  // Parameters (adjust these for different effects)
  const startTime = audioContext.currentTime; // Start time of the sound
  const duration = 2;                    // Total duration in seconds
  const baseFrequency = 220;            // Frequency of the engine sound (Hz)
  const dopplerEffect = 0.1;           // Doppler shift amount (adjust this for speed)
  const volume = 0.6;                    // Overall volume (0.0 - 1.0)

  // 1. Engine Sound (Sine Wave - basic, can be improved)
  const engineOscillator = audioContext.createOscillator();
  engineOscillator.type = 'sine';  // Or 'sawtooth', 'square' for different engine sounds
  engineOscillator.frequency.setValueAtTime(baseFrequency * (1 + dopplerEffect), startTime);  // Initial frequency
  engineOscillator.frequency.linearRampToValueAtTime(baseFrequency * (1 - dopplerEffect), startTime + duration); // Doppler shift over time
  engineOscillator.start(startTime);
  engineOscillator.stop(startTime + duration);


  // 2.  Volume Envelope (for fading in and out)
  const gainNode = audioContext.createGain();
  gainNode.gain.setValueAtTime(0, startTime);          // Start silent
  gainNode.gain.linearRampToValueAtTime(volume, startTime + 0.2); // Fade in
  gainNode.gain.setValueAtTime(volume, startTime + duration - 0.2); // Sustain volume near the middle
  gainNode.gain.linearRampToValueAtTime(0, startTime + duration);   // Fade out

  // 3.  Connect the Audio Nodes
  engineOscillator.connect(gainNode);
  gainNode.connect(audioContext.destination); // Connect to the speakers

}

